                      <input type="text" value={productionForm.sku} onChange={(e) => setProductionForm(p => ({ ...p, sku: e.target.value }))} placeholder="ABC-123" className="w-full bg-slate-900 border border-slate-600 rounded-lg px-3 py-2 text-white" />
